head.script:
//
(function() {
    // 1. On page load: clean URL without reload (replaceState only changes URL bar)
    var url = window.location.href;
    if (url.indexOf('m=1') !== -1) {
        var cleanUrl = url.replace(/[?&]m=1/g, '').replace(/\?$/, '').replace(/&$/, '').replace(/\?&/, '?');
        if (cleanUrl !== url) {
            history.replaceState(null, '', cleanUrl);
        }
    }

    // 2. Intercept link clicks to strip m=1 - but skip links handled by AJAX
    document.addEventListener('click', function(e) {
        var link = e.target.closest('a[href]');
        if (!link) return;

        var href = link.getAttribute('href');
        if (!href || href.indexOf('m=1') === -1) return;

        // Skip label links and homepage - these are handled by AJAX pagination script
        if (href.indexOf('/search/label/') !== -1 || href === '/' || href === window.location.origin + '/') {
            // Just clean the href attribute, don't navigate (AJAX will handle it)
            link.setAttribute('href', href.replace(/[?&]m=1/g, '').replace(/\?$/, '').replace(/&$/, '').replace(/\?&/, '?'));
            return;
        }

        // For other links, prevent and redirect clean
        e.preventDefault();
        var cleanHref = href.replace(/[?&]m=1/g, '').replace(/\?$/, '').replace(/&$/, '').replace(/\?&/, '?');
        window.location.href = cleanHref;
    });
})();
//

