//
(function() {
    var amp = String.fromCharCode(38);

    // Detect context: homepage, label, or search
    var params = new URLSearchParams(window.location.search);
    var path = window.location.pathname;
    var isLabelPage = path.indexOf('/search/label/') === 0;
    var isSearchPage = path === '/search' && params.has('q');
    var labelName = isLabelPage ? decodeURIComponent(path.split('/search/label/')[1]) : null;

    // Dynamic Max Results (7 for Home with Hero, 6 for others)
    var maxResults = (isLabelPage || isSearchPage) ? 6 : 7;

    var currentPage = parseInt(params.get('page')) || 1;
    var grid = document.getElementById('posts-grid');
    var cache = {};
    var newerLink = document.getElementById('newer-link');
    var olderLink = document.getElementById('older-link');

    // Helper to enable/disable buttons (Hide if disabled)
    function enableBtn(btn) { btn.style.display = 'block'; btn.style.color = '#202124'; btn.style.cursor = 'pointer'; }
    function disableBtn(btn) { btn.style.display = 'none'; btn.style.cursor = 'default'; btn.onclick = null; }

    function checkPagerVisibility() {
        var pager = document.getElementById('blog-pager');
        var newer = document.getElementById('newer-link');
        var older = document.getElementById('older-link');
        if (pager && newer && older) {
            if (newer.style.display === 'none' && older.style.display === 'none') {
                pager.style.display = 'none';
            } else {
                pager.style.display = 'flex';
            }
        }
    }

    // Helper for status bar management
    function getOrCreateStatusBar() {
        var bar = document.querySelector('.search-status-wrap');
        if (!bar) {
            bar = document.createElement('div');
            bar.className = 'search-status-wrap';
            bar.innerHTML = '<div class="status-text"></div>';
            bar.style.display = 'none';
            grid.parentNode.insertBefore(bar, grid);
        }
        return bar;
    }
    function showStatus(labelText) {
        var bar = getOrCreateStatusBar();
        bar.style.display = 'block';
        bar.querySelector('.status-text').innerHTML = 'Menampilkan artikel dalam kategori <span class="status-highlight">' + labelText + '</span>';
    }
    function hideStatus() {
        var bar = document.querySelector('.search-status-wrap');
        if (bar) bar.style.display = 'none';
    }

    // Helper to toggle Homepage-specific elements (Hero and Heading)
    function toggleHomepageElements(show) {
        var hero = document.querySelector('.hero-section');
        var heading = document.querySelector('.section-heading');
        if (hero) hero.style.display = show ? 'block' : 'none';
        if (heading) heading.style.display = show ? 'block' : 'none';
    }

    // Render function
    function renderPosts(entries) {
        grid.innerHTML = '';
        entries.forEach(function(entry, index) {
            // Skip first post on Homepage Page 1 (It's the Hero)
            // Note: Since maxResults is dynamic (6 vs 7), this logic is safe.
            // If isLabelPage/SearchPage, maxResults is 6, so index 0 is valid to show.
            if (currentPage === 1 && !isLabelPage && !isSearchPage && index === 0) return;

            var url = entry.link.find(function(l) { return l.rel === 'alternate'; }).href;
            var title = entry.title.$t;
            var snippet = '';
            if (entry.content && entry.content.$t) {
                var tmp = document.createElement('div');
                tmp.innerHTML = entry.content.$t;
                snippet = tmp.textContent.substring(0, 140) + '...';
            }
            var label = entry.category ? entry.category[0].term : 'Story';
            var labelUrl = '/search/label/' + encodeURIComponent(label);
            var img = entry.media$thumbnail ? entry.media$thumbnail.url.replace('/s72-c/', '/w600/') : '';

            var card = document.createElement('article');
            card.className = 'post-card';
            card.innerHTML = 
                '<a href="' + url + '" aria-label="' + title + '" style="display:block;text-decoration:none;color:inherit">' +
                '<div class="card-media">' + (img ? '<img alt="' + title + '" src="' + img + '" loading="lazy"/>' : '<div style="width:100%;height:100%;background:#f1f3f4;display:flex;align-items:center;justify-content:center;color:#bdc1c6;"><svg style="width:48px" viewBox="0 0 24 24"><path d="M18 13v7H4V6h5.02c.05-.71.22-1.38.48-2H4c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2v-5l-2-2zm-1.5 5h-11l3.5-4.5 2.5 3.01L14.5 12l2 2.68z" fill="currentColor"></path></svg></div>') + '</div></a>' +
                '<div class="card-meta"><a href="' + labelUrl + '" style="text-decoration:none;color:inherit">' + label + '</a></div>' +
                '<a href="' + url + '" style="text-decoration:none;color:inherit"><h2 class="card-title">' + title + '</h2></a>' +
                '<div class="card-snippet">' + snippet + '</div>' +
                '<a href="' + url + '" style="text-decoration:none;margin-top:auto;width:fit-content"><div class="read-more-pill" style="margin-top:0">Read Story <svg width="18" height="18" viewBox="0 0 24 24" fill="none" style="margin-left:4px"><path d="M5 12H19M19 12L12 5M19 12L12 19" stroke="currentColor" stroke-width="2.5" stroke-linecap="round" stroke-linejoin="round"/></svg></div></a>';
            grid.appendChild(card);
        });
        window.scrollTo({top: 0, behavior: 'smooth'});
    }

    // Fetch page data
    function fetchPage(page) {
        var start = (page - 1) * maxResults + 1;
        var feedUrl;
        var searchQuery = isSearchPage ? params.get('q') : null;

        if (isLabelPage && labelName) {
            feedUrl = '/feeds/posts/default/-/' + encodeURIComponent(labelName) + '?alt=json' + amp + 'start-index=' + start + amp + 'max-results=' + maxResults;
        } else if (isSearchPage && searchQuery) {
            feedUrl = '/feeds/posts/default?alt=json' + amp + 'q=' + encodeURIComponent(searchQuery) + amp + 'start-index=' + start + amp + 'max-results=' + maxResults;
        } else {
            feedUrl = '/feeds/posts/default?alt=json' + amp + 'start-index=' + start + amp + 'max-results=' + maxResults;
        }
        return fetch(feedUrl).then(function(r) { return r.json(); });
    }

    // Prefetch and cache
    function prefetch(page) {
        if (cache[page]) return;
        fetchPage(page).then(function(data) {
            if (data.feed.entry) cache[page] = data.feed.entry;
        });
    }

    // Navigate to page instantly
    function goToPage(page, pushState) {
        currentPage = page;
        var baseUrl = isLabelPage ? path : '/';
        var newUrl = page === 1 ? baseUrl : baseUrl + '?page=' + page;
        if (pushState) history.pushState({page: page}, '', newUrl);

        // Toggle Hero: Valid only on Homepage (not Label/Search)
        if (!isLabelPage && !isSearchPage) {
            toggleHomepageElements(page === 1);
        }

        updateButtons();

        // if (page === 1) { window.location.href = baseUrl; return; } // Removed this line

        if (cache[page]) {
            renderPosts(cache[page]);
            checkOlderState();
        } else {
            grid.style.opacity = '0.5';
            fetchPage(page).then(function(data) {
                if (data.feed.entry) {
                    cache[page] = data.feed.entry;
                    renderPosts(data.feed.entry);
                    checkOlderState();
                }
                grid.style.opacity = '1';
            });
        }
    }

    // Update button states
    function updateButtons() {
        if (currentPage > 1) {
            enableBtn(newerLink);
            newerLink.onclick = function(e) { e.preventDefault(); goToPage(currentPage - 1, true); };
        } else {
            disableBtn(newerLink);
        }

        disableBtn(olderLink);
        checkPagerVisibility();
    }

    // Initial load for page 2+
    if (currentPage > 1) {
        // HIDE HERO if on Homepage Page 2+ (Server might render it, so we hide it)
        if (!isLabelPage && !isSearchPage) {
            toggleHomepageElements(false);
        }

        grid.style.opacity = '0.5';
        fetchPage(currentPage).then(function(data) {
            if (data.feed.entry) {
                cache[currentPage] = data.feed.entry;
                renderPosts(data.feed.entry);
            }
            grid.style.opacity = '1';
        });
    }

    // Check for next page
    function checkOlderState() {
        disableBtn(olderLink);
        fetchPage(currentPage + 1).then(function(data) {
            if (data.feed && data.feed.entry && data.feed.entry.length > 0) {
                cache[currentPage + 1] = data.feed.entry;
                enableBtn(olderLink);
                olderLink.onclick = function(e) { e.preventDefault(); goToPage(currentPage + 1, true); };
            } else {
                disableBtn(olderLink);
            }
            checkPagerVisibility();
        }).catch(function(err) {
            console.error('checkOlderState error:', err);
            checkPagerVisibility();
        });
    }

    // Setup buttons
    updateButtons();
    setTimeout(checkOlderState, 100);

    // AJAX Navigation Logic (Simplified)
    document.addEventListener('click', function(e) {
        // ... Link Click Handlers ...
        var link = e.target.closest('a[href*="/search/label/"]');
        var logoLink = e.target.closest('.brand-link');

        if (!link && !logoLink) return;

        // CLOSE MOBILE MENU (if open)
        var nav = document.getElementById('mobileNav');
        var btn = document.getElementById('menuBtn');
        if (nav && nav.classList.contains('active')) {
            nav.classList.remove('active');
            if (btn) btn.classList.remove('active');
        }

        var currentPath = window.location.pathname;
        var href, clickedLabel;

        if (link) {
            // Label Click
            if (currentPath !== '/' && currentPath.indexOf('/search/label/') !== 0) return;
            e.preventDefault();
            href = link.getAttribute('href');
            // Strip m=1 from href to prevent visible delay
            href = href.replace(/[?&]m=1/g, '').replace(/\?$/, '').replace(/&$/, '').replace(/\?&/, '?');
            clickedLabel = decodeURIComponent(href.split('/search/label/')[1].split('?')[0]);
            labelName = clickedLabel;
            isLabelPage = true;
        } else {
            // Logo Click
            if (currentPath === '/') return;
            if (currentPath.indexOf('/search/label/') !== 0) return;

            // CRITICAL FIX: If Hero Section is missing (started on Label page), force reload
            if (!document.querySelector('.hero-section')) return;

            e.preventDefault();
            href = '/';
            clickedLabel = null;
            labelName = null;
            isLabelPage = false;
        }

        currentPage = 1;
        cache = {};
        // Update maxResults based on new state
        maxResults = (isLabelPage) ? 6 : 7;

        history.pushState({label: clickedLabel, page: 1, home: !clickedLabel}, '', href);

        if (clickedLabel) {
            showStatus(clickedLabel);
            toggleHomepageElements(false); // Hide Hero on Label Page
        } else {
            hideStatus();
            toggleHomepageElements(true); // Show Hero on Homepage
        }

        grid.style.opacity = '0.7';
        grid.style.transition = 'opacity 0.15s';
        fetchPage(1).then(function(data) {
            if (data.feed.entry) {
                cache[1] = data.feed.entry;
                renderPosts(data.feed.entry);
                updateButtons();
                checkOlderState();
            } else {
                // Empty State AJAX Handling - Hide the "Menampilkan..." status first
                hideStatus();
                var msg = 'Belum ada artikel dalam kategori <span class="status-highlight">' + labelName + '</span>';
                grid.innerHTML = '<div class="search-status-wrap" style="display:block;margin-top:40px; grid-column: 1 / -1; width: 100%; text-align:center;"><div class="status-text">' + msg + '</div><div style="margin-top:1.5rem;display:flex;justify-content:center;"><a class="btn-home" href="/">Kembali ke Beranda</a></div></div>';
                disableBtn(newerLink);
                disableBtn(olderLink);
                checkPagerVisibility();
            }
            grid.style.opacity = '1';
        });
    });

    window.onpopstate = function(e) {
        if ((e.state && e.state.label) || (e.state && e.state.home)) {
            labelName = e.state.label || null;
            isLabelPage = !!e.state.label;
            currentPage = e.state.page || 1;
            cache = {};

            // Update maxResults
            maxResults = (isLabelPage) ? 6 : 7;

            if(labelName) {
                showStatus(labelName);
                toggleHomepageElements(false);
            } else {
                hideStatus();
                toggleHomepageElements(true);
            }

            fetchPage(currentPage).then(function(data) {
                if (data.feed.entry) {
                    renderPosts(data.feed.entry);
                    updateButtons();
                    checkOlderState();
                } else {
                    if (isLabelPage) {
                        hideStatus();
                        var msg = 'Belum ada artikel dalam kategori <span class="status-highlight">' + labelName + '</span>';
                        grid.innerHTML = '<div class="search-status-wrap" style="display:block;margin-top:40px; grid-column: 1 / -1; width: 100%; text-align:center;"><div class="status-text">' + msg + '</div><div style="margin-top:1.5rem;display:flex;justify-content:center;"><a class="btn-home" href="/">Kembali ke Beranda</a></div></div>';
                    }
                    disableBtn(newerLink);
                    disableBtn(olderLink);
                    checkPagerVisibility();
                }
            });
        } else if (e.state && e.state.page) {
            goToPage(e.state.page, false);
        } else {
            window.location.reload();
        }
    };
})();
