body.script:
//
    document.addEventListener('DOMContentLoaded', function() {

        // 0. AUTO YEAR
        var yearSpan = document.getElementById("year");
        if(yearSpan) yearSpan.textContent = new Date().getFullYear();

        // 1. MOBILE MENU SYNC & LOGIC
        var navLinks = document.querySelector('.nav-links');
        var mobileNav = document.getElementById('mobileNav');
        if(navLinks && mobileNav) {
            mobileNav.innerHTML = navLinks.innerHTML;

            // Mobile Dropdown Toggle
            mobileNav.addEventListener('click', function(e) {
                var target = e.target.closest('a');
                if(!target) return;
                var parent = target.parentElement;
                if(parent && parent.classList.contains('nav-item-dropdown')) {
                    e.preventDefault();
                    parent.classList.toggle('expanded');
                }
            });
        }

        // 2. RELATED POSTS (Smart Random)
        var relatedContainer = document.getElementById('related-posts-container');
        if (relatedContainer && typeof relatedLabels !== 'undefined' && relatedLabels.length > 0) {
            var randomLabel = relatedLabels[Math.floor(Math.random() * relatedLabels.length)];
            var maxPosts = 3; 
            var fetchCount = 8; // Fetch more for variety

            // Shuffle Helper
            function shuffle(array) {
                for (var i = array.length - 1; i > 0; i--) {
                    var j = Math.floor(Math.random() * (i + 1));
                    var t = array[i]; array[i] = array[j]; array[j] = t;
                }
                return array;
            }

            var script = document.createElement('script');
            script.src = '/feeds/posts/default/-/' + encodeURIComponent(randomLabel) + '?alt=json-in-script&callback=renderRelated&max-results=' + fetchCount;
            document.body.appendChild(script);

            window.renderRelated = function(json) {
                var entry = json.feed.entry;
                if (!entry) return;

                shuffle(entry);
                var html = '<div class="related-posts-wrap"><h3 class="related-title">You might also like</h3><div class="related-grid">';
                var count = 0;

                entry.forEach(function(post) {
                    if (count >= maxPosts) return;
                    var id = post.id.$t.split('post-')[1];
                    if (typeof currentPostId !== 'undefined' && id === currentPostId) return; // Skip current

                    var link = post.link.find(l => l.rel === 'alternate').href;
                    var title = post.title.$t;
                                        var thumb = post.media$thumbnail ? post.media$thumbnail.url.replace(/\/s\d+(-[a-z0-9]+)*\//, '/w800/') : '';
                    var thumbHtml = thumb ? '<img src="' + thumb + '" loading="lazy" style="width:100%;height:100%;object-fit:cover"/>' : '<div style="width:100%;height:100%;background:#f1f3f4;display:flex;align-items:center;justify-content:center;color:#bdc1c6;"><svg style="width:48px" viewBox="0 0 24 24"><path d="M18 13v7H4V6h5.02c.05-.71.22-1.38.48-2H4c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2v-5l-2-2zm-1.5 5h-11l3.5-4.5 2.5 3.01L14.5 12l2 2.68z" fill="currentColor"></path></svg></div>';

                    html += '<div class="related-card"><a href="' + link + '"><div class="related-thumb">' + thumbHtml + '</div><h4>' + title + '</h4></a></div>';
                    count++;
                });

                if (count > 0) {
                    html += '</div></div>';
                    relatedContainer.innerHTML = html;
                }
            };
        }

        // 3. HIGH-RES IMAGE UPGRADE (Single Post Only)
        // Upgrades Blogger image URLs from limited sizes (s200, s320, s400, s640, etc) to s1600 for crisp display
        document.querySelectorAll('.sp-body img').forEach(function(img) {
            var src = img.src || img.getAttribute('src');
            if (src && (src.includes('blogger.googleusercontent.com') || src.includes('bp.blogspot.com'))) {
                // Pattern: /s640/, /s400-c/, /w640-h480/, etc.
                var upgraded = src.replace(/\/s\d+(-c)?(-rw)?(-e\d+)?(-no)?\//, '/s1600/')
                                  .replace(/\/w\d+-h\d+(-[a-z0-9]+)*\//, '/s1600/');
                if (upgraded !== src) {
                    img.src = upgraded;
                }
            }
        });

        // 4. AUTO CAPTION
        document.querySelectorAll('.sp-body img').forEach(function(img) {
            var title = img.getAttribute('title');
            if (title && title.trim() !== '') {
                var parent = img.parentElement;
                if (!parent.closest('.tr-caption-container')) {
                    var caption = document.createElement('div');
                    caption.className = 'tr-caption';
                    caption.textContent = title;
                    if (parent.tagName === 'A') parent.after(caption);
                    else img.after(caption);
                }
            }
        });

        // 4. ADMIN & AUTHOR CHECK
        // Admin Check (CSS Probe Method)
        (function() {
            var blogId = "<data:blog.blogId/>"; // Server-side preferred
            if (!blogId) {
                 // Client-side fallback logic omitted for brevity/speed as server side usually works
            }
            if(blogId) {
                var testDiv = document.createElement('div');
                var testClass = 'admin-check-' + Date.now();
                testDiv.className = testClass + ' blog-admin';
                testDiv.innerHTML = '<style>.' + testClass + '{display:none}</style>';
                document.body.appendChild(testDiv);

                var link = document.createElement('link');
                link.rel = 'stylesheet';
                link.href = 'https://www.blogger.com/dyn-css/authorization.css?targetBlogID=' + blogId;
                link.onload = function() {
                    if (window.getComputedStyle(testDiv).display === 'block') {
                        document.body.classList.add('is-admin');
                    }
                    testDiv.remove();
                };
                document.head.appendChild(link);
            }
        })();

        // Author Badge (Profile Match)
        var authorEl = document.querySelector('.author-name[data-author-url]');
        if (authorEl) {
            var url = authorEl.getAttribute('data-author-url');
            document.querySelectorAll('.comment[data-author-url="' + url + '"]').forEach(c => c.classList.add('blog-author'));
        }

    });

    // GLOBAL FUNCTIONS (Exposed for HTML Event Handlers)
    window.toggleMenu = function() {
        document.getElementById('mobileNav').classList.toggle('active');
        document.getElementById('menuBtn').classList.toggle('active');
    };

    window.toggleSearch = function() {
        var s = document.getElementById('searchBar');
        s.classList.toggle('active');
        if(s.classList.contains('active')) s.querySelector('input').focus();
    };

    window.moveReplyForm = function(cId) {
        var form = document.getElementById('custom-comment-form');
        var target = document.getElementById('c' + cId);
        var starter = target.querySelector('.comment-starter') || target;
        var replies = starter.querySelector('.comment-replies');

        if(replies) starter.insertBefore(form, replies);
        else starter.appendChild(form);

        var iframe = form.querySelector('iframe');
        iframe.src = iframe.src.split('&parentID')[0] + '&parentID=' + cId;
        form.querySelector('.btn-cancel').style.display = 'inline-block';
    };

    window.cancelReply = function() {
        var form = document.getElementById('custom-comment-form');
        document.getElementById('comments').appendChild(form);
        var iframe = form.querySelector('iframe');
        iframe.src = iframe.src.split('&parentID')[0];
        form.querySelector('.btn-cancel').style.display = 'none';
    };

    // Reading Time Estimation
    (function() {
        var readingTimeEl = document.getElementById('reading-time');
        var postBody = document.querySelector('.sp-body');
        if (readingTimeEl && postBody) {
            var text = postBody.textContent || postBody.innerText;
            var wordCount = text.trim().split(/\s+/).length;
            var readingTime = Math.ceil(wordCount / 200); // 200 words per minute
            readingTimeEl.textContent = readingTime + ' min read';
        }
    })();
