(function() {
    var skeleton = document.getElementById('skeletonOverlay');
    if (!skeleton) return;

    // Show skeleton on article link clicks
    document.addEventListener('click', function(e) {
        // Check for article links (post URLs contain year/month pattern)
        var link = e.target.closest('a[href*="/20"]');
        if (!link) return;

        var href = link.getAttribute('href');
        // Only trigger for single post URLs (contains .html or year/month/title pattern)
        if (href && (href.indexOf('.html') !== -1 || /\/\d{4}\/\d{2}\//.test(href))) {
            // Don't trigger for same page anchors or external links
            if (href.indexOf('#') === 0 || href.indexOf('http') === 0 && href.indexOf(window.location.hostname) === -1) return;

            skeleton.classList.add('active');
        }
    });

    // Hide skeleton when navigating back
    window.addEventListener('pageshow', function(e) {
        if (e.persisted) {
            skeleton.classList.remove('active');
        }
    });
})();
